const NavbarLink = ({ label, to, activeOnlyWhenExact }) => (
    <Route path={to} exact={activeOnlyWhenExact} children={({ match }) => (
        <span className={match ? 'active' : ''}>
            <Link className="LI_SPAN text-dark px-1 py-1" to={to}>{label}</Link>
        </span>
    )} />
)




